mapper.select:
SELECT count(*) FROM VACATION_REG V JOIN EMPLOYEES E ON V.EMP_ID = E.EMP_ID WHERE V.STATE != '취소'
AND V.STATE = #{state}
AND E.DEPT_ID = ( SELECT DEPT_ID FROM EMPLOYEES WHERE EMP_ID = #{empId})

SELECT * FROM ( SELECT rownum as num, r.* FROM ( SELECT V.REG_ID AS regId, V.STATE AS state, V.START_DATE AS startDate, V.END_DATE AS endDate, V.REG_DATE AS regDate, E.NAME AS empName, ROWNUM AS rnum FROM VACATION_REG V JOIN EMPLOYEES E ON V.EMP_ID = E.EMP_ID WHERE V.STATE != '취소'
AND V.STATE = #{state}
AND E.DEPT_ID = (SELECT DEPT_ID FROM EMPLOYEES WHERE EMP_ID = #{empId}) ORDER BY regDate DESC ) r ) WHERE num BETWEEN #{startNum} AND #{endNum}

SELECT * FROM ( SELECT V.REG_ID AS regId, V.STATE AS state, V.START_DATE AS startDate, V.END_DATE AS endDate, V.REG_DATE AS regDate, E.NAME AS empName, ROWNUM AS rnum FROM VACATION_REG V JOIN EMPLOYEES E ON V.EMP_ID = E.EMP_ID WHERE V.STATE != '취소'
AND V.STATE = #{state}
AND E.DEPT_ID = (SELECT DEPT_ID FROM EMPLOYEES WHERE EMP_ID = #{empId}) ORDER BY regDate DESC )

mapper.update:
UPDATE VACATION_REG 
SET STATE = #{state} 

, DENIED_CONTENT = #{deniedContent} 

WHERE REG_ID = #{regId}

mapper.insert:
INSERT INTO VACATION_REG (
    reg_id, state, start_date, end_date, reg_date, denied_content, content, emp_id, type_id
) VALUES (
    #{regId}, #{state, jdbcType=VARCHAR}, #{startDate,jdbcType=DATE}, #{endDate, jdbcType=DATE},
    #{regDate, jdbcType=DATE}, #{deniedContent, jdbcType=VARCHAR},
    #{content}, #{empId}, #{typeId, jdbcType=NUMERIC}
)
